/*** Copyright (C) 2011-2014 Wang Shiliang* All rights reserved* filename : Day.java* description : The class for Day* * created by Wang Shiliang at 5/4/2012 20:19:50**/package org.seedsofempowerment.farmsim;import java.util.Random;//This class is used for store the information about the datepublic class Day {	private int season;	private int lastDay;	private int lastMonth;	private int lastYear;	private int month;	private String monthString;	private int day;	private int climate;	public long dayStartTime;	private Random rand;		private int[] monthDay;	public static int SPRING = 0;	public static int SUMMER = 1;	public static int AUTUMN = 2;	public static int WINTER = 3;	public static int SUNNY = 0;	public static int CLOUDY = 1;	public static int RAINY = 2;	public static int STORM = 3;		public Day(){		season = SPRING;		month = 3;		day = 1;		lastDay = 1;		lastMonth = 0;		lastYear = 0;		climate = SUNNY;		dayStartTime = System.currentTimeMillis();		monthDay = new int[13];		monthDay[1] = 31;		monthDay[2] = 28;		monthDay[3] = 31;		monthDay[4] = 30;		monthDay[5] = 31;		monthDay[6] = 30;		monthDay[7] = 31;		monthDay[8] = 31;		monthDay[9] = 30;		monthDay[10] = 31;		monthDay[11] = 30;		monthDay[12] = 31;		rand = new Random();	}		public void logic(){		++day;		++lastDay;		if(day >= monthDay[month]){			day = 1;			lastDay = 1;			++month;			++lastMonth;		}		if(month > 12){			month = 1;		}		if(lastMonth > 11){			++lastYear;			lastMonth = 0;		}		if(month >=3 && month <=5){			season = SPRING;		}		else if(month >=6 && month <=8){			season = SUMMER;		}		else if(month >=9 && month <=11){			season = AUTUMN;		}		else{			season = WINTER;		}		//the climate will change every three day		if(season == SPRING){			if(day % 3 == 0){				int rand = randNumber();				if(rand > 0 && rand <= 50){					climate = 0;				}				else if(rand > 50 && rand <= 90){					climate = 1;				}				else if(rand > 90 && rand <= 99){					climate = 2;				}				else if(rand > 99){					climate = 3;				}			}		}		else if(season == SUMMER){			if(day % 3 == 0){				int rand = randNumber();				if(rand > 0 && rand <= 30){					climate = 0;				}				else if(rand > 30 && rand <= 70){					climate = 1;				}				else if(rand > 70 && rand <= 95){					climate = 2;				}				else if(rand > 95){					climate = 3;				}			}		}		else if(season == AUTUMN){			if(day % 3 == 0){				int rand = randNumber();				if(rand > 0 && rand <= 60){					climate = 0;				}				else if(rand > 60 && rand <= 90){					climate = 1;				}				else if(rand > 90 && rand <= 99){					climate = 2;				}				else if(rand > 99){					climate = 3;				}			}		}		else if(season == WINTER){			if(day % 3 == 0){				int rand = randNumber();				if(rand > 0 && rand <= 60){					climate = 0;				}				else if(rand > 60 && rand <= 90){					climate = 1;				}				else if(rand > 90 && rand <= 99){					climate = 2;				}				else if(rand > 99){					climate = 3;				}			}		}	}		public int getDay(){		return this.day;	}		public int getMonth(){		return this.month;	}		public int getSeason(){		return this.season;	}		public int getClimate(){		return this.climate;	}		public String getMonthString(){		String monthString = "";		if(month == 1){			monthString = "January";		}		else if(month == 2){			monthString = "February";		}		else if(month == 3){			monthString = "March";		}		else if(month == 4){			monthString = "April";		}		else if(month == 5){			monthString = "May";		}		else if(month == 6){			monthString = "June";		}		else if(month == 7){			monthString = "July";		}		else if(month == 8){			monthString = "August";		}		else if(month == 9){			monthString = "September";		}		else if(month == 10){			monthString = "October";		}		else if(month == 11){			monthString = "November";		}		else if(month == 12){			monthString = "December";		}		return monthString;	}		public void setDay(int day){		this.day = day;	}		public void setMonth(int month){		this.month = month;	}		public void setSeason(int season){		this.season = season;	}		public void setClimate(int climate){		this.climate = climate;	}		public int getLastDay(){		return lastDay;	}		public void setLastDay(int lastDay){		this.lastDay = lastDay;	}		public int getLastMonth(){		return lastMonth;	}		public void setLastMonth(int lastMonth){		this.lastMonth = lastMonth;	}		public int getLastYear(){		return lastYear;	}		public void setLastYear(int lastYear){		this.lastYear = lastYear;	}		//get the random number	private int randNumber(){		int ran = Math.abs(rand.nextInt());		int returnRan = ran%(100) + 1;		return returnRan;	}	}